function getLocation(n){document.getElementById("mealType").value=n;navigator.geolocation?navigator.geolocation.getCurrentPosition(showPosition):alert("Geolocation is not supported by this browser.")}function showPosition(n){var t=n.coords.latitude,i=n.coords.longitude,r=new google.maps.Geocoder,u=new google.maps.LatLng(t,i);r.geocode({latLng:u},function(n,t){if(t==google.maps.GeocoderStatus.OK){var i=n[0].formatted_address;document.getElementById("addr-input").value=i;checkMealType()}else alert("There was a problem finding your location.")})}function checkMealType(){var n=document.getElementById("mealType").value;n!=""&&document.getElementById("search-btn").click()}document.getElementById("location-icon").addEventListener("touchstart",n=>{n.preventDefault(),n.target.dispatchEvent(new Event("click"))});